import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "../components/Home.css"; // Make sure this file exists

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [activeTab, setActiveTab] = useState("current");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    useEffect(() => {
        fetchMatches("current");
    }, []);

    const fetchMatches = async (type) => {
        setActiveTab(type);
        setLoading(true);
        setError("");

        let date = new Date();
        if (type === "recent") date.setDate(date.getDate() - 1);
        if (type === "upcoming") date.setDate(date.getDate() + 1);

        const formattedDate = date.toISOString().split("T")[0];

        try {
            const response = await axios.get(`http://localhost:5001/api/matches/${formattedDate}`);
            console.log("ðŸŸ¢ [FRONTEND] Data received:", response.data);
            setMatches(response.data.results || []);
        } catch (err) {
            console.error("ðŸ”´ [FRONTEND ERROR] Failed to fetch matches:", err.message);
            setError("Failed to load matches.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="home-container">
            <h1>Live Matches</h1>
            <div className="tabs">
                <button className={activeTab === "current" ? "active" : ""} onClick={() => fetchMatches("current")}>
                    Current
                </button>
                <button className={activeTab === "recent" ? "active" : ""} onClick={() => fetchMatches("recent")}>
                    Recent
                </button>
                <button className={activeTab === "upcoming" ? "active" : ""} onClick={() => fetchMatches("upcoming")}>
                    Upcoming
                </button>
            </div>

            {error && <p className="error">{error}</p>}
            {loading && <p>Loading matches...</p>}

            <div className="matches-list">
                {matches.length > 0 ? (
                    matches.map((match) => {
                        const teams = match.sport_event?.competitors || [];
                        const team1 = teams[0]?.name || "Unknown";
                        const team2 = teams[1]?.name || "Unknown";
                        const winner = teams.find(t => t.id === match.sport_event_status?.winner_id)?.name || "Unknown";
                        return (
                            <div className="match-card" key={match.sport_event.id}>
                                <Link to={`/match/${match.sport_event.id}`} className="match-link">
                                    <h2>{team1} vs {team2}</h2>
                                </Link>
                                <p>Status: {match.sport_event_status?.match_status || "Unknown"}</p>
                                <p>Winner: {winner}</p>
                            </div>
                        );
                    })
                ) : (
                    <p>No matches available.</p>
                )}
            </div>
        </div>
    );
};

export default Home;
