import React, { useEffect, useState } from "react";
import axios from "axios";

const LiveScore = ({ matchId }) => {
  const [liveScore, setLiveScore] = useState(null);

  useEffect(() => {
    axios.get(`${process.env.REACT_APP_API_BASE_URL}/${matchId}/live-score`)
      .then(response => setLiveScore(response.data))
      .catch(error => console.log(error));
  }, [matchId]);

  if (!liveScore) return <p>Fetching live score...</p>;

  return (
    <div>
      <h3>Live Score</h3>
      <p>{liveScore.sport_event_status.display_score}</p>
    </div>
  );
};

export default LiveScore;
