import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./MatchPage.css";

const MatchPage = () => {
  const { matchId } = useParams();
  const [matchDetails, setMatchDetails] = useState(null);
  const [timeline, setTimeline] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:5001/api/match/${matchId}`)
      .then((response) => {
        setMatchDetails(response.data);
      })
      .catch((error) => console.error("Error fetching match details:", error));

    axios.get(`http://localhost:5001/api/match/${matchId}/timeline`)
      .then((response) => {
        setTimeline(response.data.timeline || []);
      })
      .catch((error) => console.error("Error fetching timeline:", error));
  }, [matchId]);

  if (!matchDetails) return <p>Loading match details...</p>;

  const { sport_event, sport_event_status } = matchDetails;
  const teams = sport_event.competitors;

  return (
    <div className="match-container">
      <h2>{sport_event.season.name}</h2>
      <p>Status: {sport_event_status.status.toUpperCase()}</p>

      <div className="scorecard">
        <div className="team">
          <h3>{teams[0].name}</h3>
          <p>{sport_event_status.period_scores[0].display_score}</p>
        </div>
        <span className="vs">VS</span>
        <div className="team">
          <h3>{teams[1].name}</h3>
          <p>{sport_event_status.period_scores[1].display_score}</p>
        </div>
      </div>

      <h3>Ball-by-Ball Commentary</h3>
      <ul>
        {timeline.length > 0 ? timeline.map((event, index) => (
          <li key={index}>{event.description}</li>
        )) : <p>No commentary available.</p>}
      </ul>
    </div>
  );
};

export default MatchPage;
