import React, { useState, useEffect } from "react";
import axios from "axios";

const Tabs = ({ matchId }) => {
  const [activeTab, setActiveTab] = useState("scorecard");
  const [data, setData] = useState(null);

  useEffect(() => {
    let url = activeTab === "scorecard"
      ? `http://localhost:5001/api/matches/${matchId}/summary`
      : `http://localhost:5001/api/matches/${matchId}/timeline`;

    axios.get(url)
      .then(response => setData(response.data))
      .catch(error => console.error(`Error fetching ${activeTab} data`, error));
  }, [activeTab, matchId]);

  return (
    <div>
      <div className="tabs">
        <button onClick={() => setActiveTab("scorecard")}>Scorecard</button>
        <button onClick={() => setActiveTab("commentary")}>Commentary</button>
      </div>

      {data ? (
        <pre>{JSON.stringify(data, null, 2)}</pre>
      ) : (
        <p>Loading {activeTab}...</p>
      )}
    </div>
  );
};

export default Tabs;
