import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMatchDetails = async () => {
            try {
                console.log(`üü° Fetching match details for: ${matchId}`);
                const response = await axios.get(`http://localhost:5001/api/matches/${matchId}/timeline`);
                setMatchDetails(response.data);
            } catch (err) {
                setError("Failed to load match details");
                console.error("‚ùå Error fetching match details:", err);
            }
        };

        fetchMatchDetails();
    }, [matchId]);

    if (error) return <h2>{error}</h2>;
    if (!matchDetails) return <h2>Loading match details...</h2>;

    // Extracting necessary data
    const { league, venue, scheduled, status, sport_event_status, competitors } = matchDetails;

    // Extract teams and IDs
    const homeTeam = competitors?.[0]?.name || "Unknown Home Team";
    const awayTeam = competitors?.[1]?.name || "Unknown Away Team";
    const homeTeamId = competitors?.[0]?.id || "N/A";
    const awayTeamId = competitors?.[1]?.id || "N/A";

    // Identify Toss Winner
    const tossWinner = sport_event_status?.toss_won_by === homeTeamId ? homeTeam : awayTeam;
    const tossDecision = sport_event_status?.toss_decision || "N/A";

    // Identify Match Winner
    const matchWinner = sport_event_status?.winner_id === homeTeamId ? homeTeam :
                        sport_event_status?.winner_id === awayTeamId ? awayTeam : "N/A";

    // Get Scores
    const scores = sport_event_status?.period_scores || [];
    const homeScore = scores[1]?.display_score || "N/A"; // Assuming 2nd innings score for home
    const awayScore = scores[0]?.display_score || "N/A"; // Assuming 1st innings score for away

    return (
        <div className="match-details">
            <h1>Match Details</h1>
            <h2>{homeTeam} vs {awayTeam}</h2>
            <p><strong>League:</strong> {league}</p>
            <p><strong>Venue:</strong> {venue || "Unknown Venue"}</p>
            <p><strong>Scheduled:</strong> {scheduled}</p>
            <p><strong>Status:</strong> {status}</p>
            <p><strong>Toss Winner:</strong> {tossWinner} ({tossDecision})</p>
            <p><strong>Match Winner:</strong> {matchWinner}</p>

            <h3>Scores:</h3>
            <p><strong>{homeTeam}:</strong> {homeScore}</p>
            <p><strong>{awayTeam}:</strong> {awayScore}</p>
        </div>
    );
};

export default MatchPage;
