import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../components/MatchPage.css";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        fetchMatchDetails();
    }, [matchId]);

    const fetchMatchDetails = async () => {
        try {
            const response = await axios.get(`http://localhost:5001/api/match/${matchId}`);
            console.log("ðŸŸ¢ [FRONTEND] Match details received:", response.data);
            setMatchDetails(response.data);
        } catch (err) {
            console.error("ðŸ”´ [FRONTEND ERROR] Failed to fetch match details:", err.message);
            setError("Failed to load match details.");
        } finally {
            setLoading(false);
        }
    };

    if (loading) return <p>Loading match details...</p>;
    if (error) return <p className="error">{error}</p>;

    return (
        <div className="match-page">
            <h1>{matchDetails?.sport_event?.season?.name || "Match Details"}</h1>
            <p>Status: {matchDetails?.sport_event_status?.match_status || "Unknown"}</p>
            <p>Score: {matchDetails?.sport_event_status?.display_score || "N/A"}</p>
            <p>Overs: {matchDetails?.sport_event_status?.display_overs || "N/A"}</p>
            <p>Winner: {matchDetails?.sport_event_status?.winner_id || "Unknown"}</p>
        </div>
    );
};

export default MatchPage;
