import React, { useEffect, useState } from "react";
import axios from "axios";

const MatchDetails = ({ matchId }) => {
  const [matchDetails, setMatchDetails] = useState(null);

  useEffect(() => {
    axios.get(`${process.env.REACT_APP_API_BASE_URL}/${matchId}`)
      .then(response => setMatchDetails(response.data))
      .catch(error => console.log(error));
  }, [matchId]);

  if (!matchDetails) return <p>Loading match details...</p>;

  return (
    <div>
      <h2>{matchDetails.sport_event.season.name}</h2>
      <p>Status: {matchDetails.sport_event_status.match_status}</p>
      <p>Score: {matchDetails.sport_event_status.display_score}</p>
    </div>
  );
};

export default MatchDetails;
