import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MatchPage = () => {
  const { matchId } = useParams();
  const [matchDetails, setMatchDetails] = useState(null);
  const [activeTab, setActiveTab] = useState("summary");

  useEffect(() => {
    axios
      .get(`http://localhost:5001/api/match/${matchId}`)
      .then((response) => {
        console.log("[MATCH DETAILS]", response.data); // Debugging Log
        setMatchDetails(response.data);
      })
      .catch((error) => console.error("Error fetching match details:", error));
  }, [matchId]);

  if (!matchDetails) return <p>Loading match details...</p>;

  return (
    <div>
      <h2>{matchDetails.sport_event.competitors[0].name} vs {matchDetails.sport_event.competitors[1].name}</h2>
      <p>Status: {matchDetails.sport_event_status.status}</p>
      <p>Winner: {matchDetails.sport_event_status.winner_id ? matchDetails.sport_event_status.winner_id : "TBD"}</p>

      {/* Tabs */}
      <div>
        <button onClick={() => setActiveTab("summary")}>Summary</button>
        <button onClick={() => setActiveTab("scorecard")}>Scorecard</button>
        <button onClick={() => setActiveTab("commentary")}>Commentary</button>
      </div>

      {/* Tab Content */}
      {activeTab === "summary" && (
        <div>
          <h3>Match Summary</h3>
          <p>{matchDetails.sport_event.season.name}</p>
          <p>{matchDetails.sport_event.scheduled}</p>
        </div>
      )}

      {activeTab === "scorecard" && (
        <div>
          <h3>Scorecard</h3>
          {matchDetails.sport_event_status.period_scores.map((inning, index) => (
            <p key={index}>
              Inning {inning.number}: {inning.display_score} ({inning.away_wickets} wickets)
            </p>
          ))}
        </div>
      )}

      {activeTab === "commentary" && (
        <div>
          <h3>Commentary (Coming Soon)</h3>
          <p>Ball-by-ball updates will be added here.</p>
        </div>
      )}
    </div>
  );
};

export default MatchPage;
