import React from 'react';

const Scorecard = ({ homeTeam, awayTeam, type }) => {
  return (
    <div className="scorecard">
      <h3>{type === 'batting' ? 'Batting' : 'Bowling'} Scorecard</h3>

      {/* ‚úÖ Table for Home Team */}
      <h4>üè† Home Team</h4>
      <table>
        <thead>
          <tr>
            {type === 'batting' ? (
              <>
                <th>Player</th>
                <th>Runs</th>
                <th>Balls</th>
                <th>Fours</th>
                <th>Sixes</th>
                <th>SR</th>
                <th>Dismissal</th>
              </>
            ) : (
              <>
                <th>Player</th>
                <th>Overs</th>
                <th>Runs</th>
                <th>Wickets</th>
                <th>Economy</th>
              </>
            )}
          </tr>
        </thead>
        <tbody>
          {homeTeam.map((player) => (
            <tr key={player.name}>
              {Object.values(player).map((value) => (
                <td key={value}>{value}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {/* ‚úÖ Table for Away Team */}
      <h4>üèÜ Away Team</h4>
      <table>
        <thead>
          {/* Same structure */}
        </thead>
        <tbody>
          {awayTeam.map((player) => (
            <tr key={player.name}>
              {Object.values(player).map((value) => (
                <td key={value}>{value}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Scorecard;
