const Scorecard = ({ data }) => {
  if (!data) {
    return <p>No scorecard data available.</p>;
  }

  const renderTable = (team, title) => (
    <div className="scorecard-section">
      <h3>{title}</h3>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Runs</th>
            <th>Balls</th>
            <th>Fours</th>
            <th>Sixes</th>
            <th>Strike Rate</th>
            <th>Dismissal</th>
          </tr>
        </thead>
        <tbody>
          {team?.map((player, index) => (
            <tr key={index}>
              <td>{player.name || '-'}</td>
              <td>{player.runs || '-'}</td>
              <td>{player.balls || '-'}</td>
              <td>{player.fours || '-'}</td>
              <td>{player.sixes || '-'}</td>
              <td>{player.strike_rate || '-'}</td>
              <td>{player.dismissal || '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div>
      {renderTable(data.home_team, data.home_team_name || 'Home Team')}
      {renderTable(data.away_team, data.away_team_name || 'Away Team')}
    </div>
  );
};

export default Scorecard;
