import { createContext, useContext, useState, useEffect } from 'react';
import { loginUser, logoutUser, getProfile } from '../api/authService';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    const loadUser = async () => {
      try {
        console.log('Fetching user profile');
        const profile = await getProfile(); // ✅ Load user profile from API
        setUser(profile);
        setIsAuthenticated(true);
        setIsAdmin(profile.role === 'admin');
      } catch (error) {
        console.error('Failed to load user profile:', error);
        setIsAuthenticated(false);

        // ✅ Auto-refresh token if expired
        const refreshToken = localStorage.getItem('refreshToken');
        if (refreshToken) {
          try {
            console.log('Refreshing token...');
            const response = await axios.post(
              `${process.env.REACT_APP_API_URL}/api/user/refresh-token`,
              { refreshToken }
            );

            const { token, refreshToken: newRefreshToken } = response.data;

            // ✅ Store new tokens
            localStorage.setItem('token', token);
            localStorage.setItem('refreshToken', newRefreshToken);

            await loadUser(); // ✅ Reload profile after refreshing token
          } catch (refreshError) {
            console.error('Token refresh failed:', refreshError);
            logout(); // ✅ Logout if refresh fails
          }
        }
      }
    };

    loadUser();
  }, []);


  // ✅ Login Function
  const login = async (username, password) => {
    try {
      console.log('Submitting login request...');
      const data = await loginUser({username, password})
      const { token, refreshToken, user } = response.data;

      setUser({
        token,
        username: user.username,
        role: user.role,
        phone: user.phone,
        credits: user.credits,
      });

      // ✅ Store tokens
      localStorage.setItem('token', token);
      localStorage.setItem('refreshToken', refreshToken);
      localStorage.setItem('role', user.role);

      setIsAuthenticated(true);
      setIsAdmin(user.role === 'admin');

      console.log('Login successful!');
    } catch (error) {
      console.error('Login failed:', error);
      throw error;
    }
  };


  // ✅ Logout Function
  const logout = async () => {
    try {
      console.log('Sending logout request...');
      await logoutUser();

      localStorage.removeItem('token');
      localStorage.removeItem('refreshToken');
      localStorage.removeItem('role');

      setUser(null);
      setIsAuthenticated(false);
      setIsAdmin(false);

      console.log('Logged out successfully');
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  return (
    <AuthContext.Provider value={{ user, isAuthenticated, isAdmin, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

// ✅ Hook to Access Auth Context
export const useAuth = () => useContext(AuthContext);

// ✅ Proper Default Export
export default AuthContext;
