import React, { useEffect, useState } from "react";
import axios from "axios";
import MatchCard from "../components/MatchCard";

const Home = () => {
  const [matches, setMatches] = useState([]);
  const [activeTab, setActiveTab] = useState("current");

  // Fetch matches based on active tab
  const fetchMatches = (selectedTab) => {
    setActiveTab(selectedTab);

    let date;
    if (selectedTab === "current") {
      date = new Date().toISOString().split("T")[0]; // Today
    } else if (selectedTab === "recent") {
      const yesterday = new Date();
      yesterday.setDate(yesterday.getDate() - 1);
      date = yesterday.toISOString().split("T")[0]; // Yesterday
    } else if (selectedTab === "upcoming") {
      const tomorrow = new Date();
      tomorrow.setDate(tomorrow.getDate() + 1);
      date = tomorrow.toISOString().split("T")[0]; // Tomorrow
    }

    console.log(`Fetching matches for: ${date}`);

    axios.get(`http://localhost:5001/api/matches/${date}`)
      .then(res => {
        setMatches(res.data);
      })
      .catch(err => console.error(`Error fetching ${selectedTab} matches:`, err));
  };

  // Fetch current matches on load
  useEffect(() => {
    fetchMatches("current");
  }, []);

  return (
    <div>
      <h1>Live Matches</h1>

      {/* Tabs */}
      <div className="tabs">
        <button onClick={() => fetchMatches("current")} className={activeTab === "current" ? "active" : ""}>
          Current
        </button>
        <button onClick={() => fetchMatches("recent")} className={activeTab === "recent" ? "active" : ""}>
          Recent
        </button>
        <button onClick={() => fetchMatches("upcoming")} className={activeTab === "upcoming" ? "active" : ""}>
          Upcoming
        </button>
      </div>

      {/* Matches List */}
      <div className="match-list">
        {matches.length === 0 ? (
          <p>No matches available.</p>
        ) : (
          matches.map(match => <MatchCard key={match.id} match={match} />)
        )}
      </div>
    </div>
  );
};

export default Home;
