import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMatchDetails = async () => {
            try {
                const response = await axios.get(`http://localhost:5001/api/match/${matchId}`);
                setMatchDetails(response.data);
            } catch (err) {
                setError("Failed to load match details.");
            }
        };

        fetchMatchDetails();
    }, [matchId]);

    if (!matchDetails) return <h2>Loading match details...</h2>;
    if (error) return <h2>{error}</h2>;

    return (
        <div className="match-detail">
            <h1>Match Details</h1>
            <h2>{matchDetails.name}</h2>
            <p>League: {matchDetails.league?.name || "N/A"}</p>
            <p>Scheduled: {matchDetails.scheduled || "N/A"}</p>
            <p>Status: {matchDetails.status || "N/A"}</p>
            <p>Winner: {matchDetails.winner_id || "TBD"}</p>
            <p>Toss: {matchDetails.toss_id || "TBD"}</p>
        </div>
    );
};

export default MatchPage;
