import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Home = () => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchMatches(new Date().toISOString().split("T")[0]);
  }, []);

  const fetchMatches = async (date) => {
    setLoading(true);
    setError("");

    try {
      const res = await axios.get(`http://localhost:5001/api/matches/${date}`);
      console.log("Fetched Matches:", res.data);

      // üõ†Ô∏è Fix: Safely handle API response
      if (res.data && res.data.results && Array.isArray(res.data.results)) {
        setMatches(res.data.results);
      } else {
        setMatches([]); // üõ†Ô∏è Set empty array if no valid data
      }
    } catch (err) {
      console.error("Error fetching matches:", err);
      setError("Failed to fetch matches. Please try again.");
      setMatches([]); // üõ†Ô∏è Ensure UI doesn't crash
    }

    setLoading(false);
  };

  return (
    <div>
      <h1>Live Matches</h1>
      <div>
        <button onClick={() => fetchMatches(new Date().toISOString().split("T")[0])}>Current</button>
        <button onClick={() => fetchMatches(new Date(new Date().setDate(new Date().getDate() - 1)).toISOString().split("T")[0])}>Recent</button>
        <button onClick={() => fetchMatches(new Date(new Date().setDate(new Date().getDate() + 1)).toISOString().split("T")[0])}>Upcoming</button>
      </div>

      {loading && <p>Loading matches...</p>}
      {error && <p>{error}</p>}
      {matches.length > 0 ? (
        matches.map((match) => (
          <div key={match.sport_event?.id || Math.random()} className="match-container">
            <Link className="match-link" to={`/match/${match.sport_event?.id || "#"}`}>
              <h2>{match.sport_event?.competitors?.[0]?.name || "Unknown"} vs {match.sport_event?.competitors?.[1]?.name || "Unknown"}</h2>
            </Link>
            <p>Status: {match.sport_event_status?.status || "N/A"}</p>
            <p>Winner: {match.sport_event_status?.winner_id || "TBD"}</p>
          </div>
        ))
      ) : (
        !loading && <p>No matches available.</p>
      )}
    </div>
  );
};

export default Home;
