import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Tabs from "../components/Tabs"; // Create Tabs Component

const MatchPage = () => {
  const { matchId } = useParams();
  const [matchDetails, setMatchDetails] = useState(null);
  const [activeTab, setActiveTab] = useState("summary");

  useEffect(() => {
    axios
      .get(`http://localhost:5001/api/match/${matchId}`)
      .then((response) => setMatchDetails(response.data))
      .catch((error) => console.error("Error fetching match details:", error));
  }, [matchId]);

  if (!matchDetails) return <p>Loading match details...</p>;

  return (
    <div className="match-page">
      <h2>{matchDetails.sport_event.competitors[0].name} vs {matchDetails.sport_event.competitors[1].name}</h2>
      <p>Status: {matchDetails.sport_event_status.status}</p>
      <p>Score: {matchDetails.sport_event_status.display_score}</p>

      <Tabs activeTab={activeTab} setActiveTab={setActiveTab} />

      {activeTab === "summary" && <div>Match Summary: {matchDetails.sport_event.season.name}</div>}
      {activeTab === "scorecard" && <div>Scorecard Data Coming Soon</div>}
      {activeTab === "commentary" && <div>Commentary Data Coming Soon</div>}
    </div>
  );
};

export default MatchPage;
