import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [error, setError] = useState(null);
    const [winnerName, setWinnerName] = useState("TBD");
    const [tossWinnerName, setTossWinnerName] = useState("TBD");

    useEffect(() => {
        const fetchMatchDetails = async () => {
            try {
                console.log(`üü° Fetching match details for: ${matchId}`);
                const response = await axios.get(`http://localhost:5001/api/match/${matchId}`);
                setMatchDetails(response.data);

                if (response.data.sport_event_status.winner_id) {
                    fetchTeamName(response.data.sport_event_status.winner_id, setWinnerName);
                }
                if (response.data.sport_event_status.toss_won_by) {
                    fetchTeamName(response.data.sport_event_status.toss_won_by, setTossWinnerName);
                }
            } catch (err) {
                console.error("‚ùå [ERROR] Could not fetch match details:", err);
                setError("Failed to load match details.");
            }
        };

        fetchMatchDetails();
    }, [matchId]);

    const fetchTeamName = async (teamId, setTeamName) => {
        try {
            console.log(`üü° Fetching team name for ID: ${teamId}`);
            const response = await axios.get(`http://localhost:5001/api/team/${teamId}`);
            setTeamName(response.data.team.name);
        } catch (err) {
            console.error(`‚ùå Error fetching team name for ${teamId}:`, err);
        }
    };

    if (error) return <h2>{error}</h2>;
    if (!matchDetails) return <h2>Loading match details...</h2>;

    return (
        <div className="match-detail">
            <h1>Match Details</h1>
            <h2>{matchDetails.sport_event.competitors[0].name} vs {matchDetails.sport_event.competitors[1].name}</h2>
            <p>League: {matchDetails.sport_event.season.name || "N/A"}</p>
            <p>Scheduled: {matchDetails.sport_event.scheduled}</p>
            <p>Status: {matchDetails.sport_event_status.status || "N/A"}</p>
            <p>Winner: {winnerName}</p>
            <p>Toss: {tossWinnerName} chose to {matchDetails.sport_event_status.toss_decision || "TBD"}</p>
        </div>
    );
};

export default MatchPage;
