import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../components/Home.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState(null);
    const [selectedTab, setSelectedTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            try {
                const response = await axios.get(`http://localhost:5001/api/matches/${selectedTab}`);
                setMatches(response.data);
            } catch (err) {
                setError("Failed to load matches.");
            }
        };

        fetchMatches();
    }, [selectedTab]);

    return (
        <div className="home-container">
            <h1>Live Matches</h1>
            <div className="tabs">
                {["current", "recent", "upcoming"].map((type) => (
                    <button
                        key={type}
                        className={selectedTab === type ? "active" : ""}
                        onClick={() => setSelectedTab(type)}
                    >
                        {type.charAt(0).toUpperCase() + type.slice(1)}
                    </button>
                ))}
            </div>

            {error ? (
                <p className="error">{error}</p>
            ) : (
                matches.map((match) => (
                    <div key={match.id} className="match-card">
                        <Link to={`/match/${match.id}`} className="match-link">
                            {match.name}
                        </Link>
                        <p>League: {match.league?.name || "N/A"}</p>
                        <p>Scheduled: {match.scheduled || "N/A"}</p>
                        <p>Status: {match.status || "N/A"}</p>
                        <p>Winner: {match.winner_id ? match.winner_id : "TBD"}</p>
                        <p>Toss: {match.toss_id ? match.toss_id : "TBD"}</p>
                    </div>
                ))
            )}
        </div>
    );
};

export default Home;
