import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Home = () => {
  const [matches, setMatches] = useState([]);  // Initialize as an empty array
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get("http://localhost:5001/api/matches")
      .then(response => {
        if (Array.isArray(response.data)) {
          setMatches(response.data);
        } else {
          console.error("Unexpected response format:", response.data);
          setMatches([]);  // Ensure it's an empty array instead of undefined
        }
        setLoading(false);
      })
      .catch(error => {
        console.error("Error fetching matches:", error);
        setMatches([]);  // Prevent undefined issues
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading matches...</p>;

  return (
    <div className="container">
      <h2>Live Matches</h2>
      {matches.length === 0 ? (
        <p>No matches available.</p>
      ) : (
        <ul>
          {matches.map((match) => (
            <li key={match.id}>
              <Link to={`/match/${match.id}`}>
                {match.tournament.name} - {match.sport_event_status?.display_score || "No Score Yet"}
              </Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Home;
