import React, { useEffect, useState } from "react";
import axios from "axios";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [date, setDate] = useState(new Date().toISOString().split("T")[0]);

    useEffect(() => {
        fetchMatches(date);
    }, [date]);

    const fetchMatches = (selectedDate) => {
        console.log(`Fetching matches for: ${selectedDate}`);
        axios.get(`http://localhost:5001/api/matches/${selectedDate}/results`)
            .then((res) => setMatches(res.data.results || []))
            .catch((err) => console.error("Error fetching matches", err));
    };

    return (
        <div>
            <h1>Live Matches</h1>
            <button onClick={() => setDate(new Date().toISOString().split("T")[0])}>Current</button>
            <button onClick={() => setDate(new Date(Date.now() - 86400000).toISOString().split("T")[0])}>Recent</button>
            <button onClick={() => setDate(new Date(Date.now() + 86400000).toISOString().split("T")[0])}>Upcoming</button>
            {matches.length > 0 ? (
                matches.map(match => (
                    <div key={match.sport_event.id}>
                        <h3>{match.sport_event.season.name}</h3>
                        <p>Status: {match.sport_event_status.status}</p>
                    </div>
                ))
            ) : (
                <p>No matches available.</p>
            )}
        </div>
    );
};

export default Home;
