import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import '../styles/pages/ProfilePage.css';

const ProfilePage = () => {
  const { user } = useAuth();
  const [credits, setCredits] = useState(0);
  const [betHistory, setBetHistory] = useState([]);

  useEffect(() => {
    // Fetch user data and bet history
    const fetchProfileData = async () => {
      try {
        const res = await fetch('/api/user/profile', {
          method: 'GET',
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });

        if (res.ok) {
          const data = await res.json();
          setCredits(data.credits || 0);
          setBetHistory(data.bets || []);
        }
      } catch (error) {
        console.error('Failed to load profile:', error);
      }
    };

    fetchProfileData();
  }, []);

  const handleRequestCredits = async () => {
    try {
      const res = await fetch('/api/user/request-credits', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ amount: 100 }), // Can adjust amount dynamically
      });

      if (res.ok) {
        alert('Credit request submitted!');
        fetchProfileData(); // Refresh data
      }
    } catch (error) {
      console.error('Failed to request credits:', error);
    }
  };

  return (
    <div className="profile-container">
      {/* User Info Section */}
      <div className="user-info">
        <img src="/user-avatar.png" alt="Profile" className="profile-pic" />
        <div>
          <h2>{user?.username}</h2>
          <p>📱 {user?.mobile || 'Not provided'}</p>
          <p>📧 {user?.email || 'Not provided'}</p>
        </div>
      </div>

      {/* Credits Section */}
      <div className="credits-section">
        <h3>💳 Credits: {credits}</h3>
        <button className="request-btn" onClick={handleRequestCredits}>
          Request Credits
        </button>
      </div>

      {/* Bet History Section */}
      <div className="bet-history">
        <h3>🎯 Bet History</h3>
        {betHistory.length > 0 ? (
          <table className="bet-table">
            <thead>
              <tr>
                <th>Match</th>
                <th>Status</th>
                <th>Outcome</th>
              </tr>
            </thead>
            <tbody>
              {betHistory.map((bet) => (
                <tr key={bet.id}>
                  <td>{bet.match}</td>
                  <td>
                    {bet.status === 'won' ? '✅ Win' : bet.status === 'lost' ? '❌ Loss' : '🔄 Pending'}
                  </td>
                  <td>{bet.outcome}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No bets placed yet.</p>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
