import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMatchDetails = async () => {
            try {
                console.log(`üü° Fetching match details for: ${matchId}`);
                const response = await axios.get(`http://localhost:5001/api/match/${matchId}`);

                if (!response.data?.sport_event) {
                    console.error("‚ùå Invalid match data:", response.data);
                    setError("Invalid match data received.");
                    return;
                }

                setMatchDetails(response.data);
            } catch (err) {
                console.error("‚ùå Fetch error:", err.message);
                setError("Failed to load match details.");
            }
        };

        fetchMatchDetails();
    }, [matchId]);

    if (error) return <h2>{error}</h2>;
    if (!matchDetails) return <h2>Loading...</h2>;

    return (
        <div className="match-detail">
            <h1>Match Details</h1>
            <h2>{matchDetails.sport_event.name}</h2>
            <p>League: {matchDetails.sport_event.season?.name || "N/A"}</p>
            <p>Scheduled: {matchDetails.sport_event.scheduled}</p>
            <p>Status: {matchDetails.sport_event_status?.status || "N/A"}</p>
            <p>Winner: {matchDetails.sport_event_status?.winner_id || "TBD"}</p>
            <p>Toss: {matchDetails.sport_event_status?.toss_won_by || "TBD"}</p>
        </div>
    );
};

export default MatchPage;
