import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "../components/Home.css"; // Import Home styles

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        fetchMatches("current");
    }, []);

    const fetchMatches = async (type) => {
        setActiveTab(type);
        let date = new Date();
        if (type === "recent") date.setDate(date.getDate() - 1);
        if (type === "upcoming") date.setDate(date.getDate() + 1);

        const formattedDate = date.toISOString().split("T")[0];

        try {
            const response = await axios.get(`http://localhost:5001/api/matches/${formattedDate}`);
            console.log("ðŸŸ¢ [FRONTEND] Matches received:", response.data);
            setMatches(response.data);
        } catch (error) {
            console.error("ðŸ”´ [ERROR] Failed to fetch matches:", error);
        }
    };

    return (
        <div className="container">
            <h1>Live Matches</h1>
            <div className="tabs">
                <button className={activeTab === "current" ? "active" : ""} onClick={() => fetchMatches("current")}>Current</button>
                <button className={activeTab === "recent" ? "active" : ""} onClick={() => fetchMatches("recent")}>Recent</button>
                <button className={activeTab === "upcoming" ? "active" : ""} onClick={() => fetchMatches("upcoming")}>Upcoming</button>
            </div>

            {matches.length > 0 ? (
                matches.map(match => (
                    <div key={match.id} className="match-card">
                        <Link to={`/match/${match.id}`}>
                            <h2>{match.teamA} vs {match.teamB}</h2>
                        </Link>
                        <p>League: {match.league}</p>
                        <p>Scheduled: {new Date(match.scheduled).toLocaleString()}</p>
                        <p>Status: {match.status}</p>
                        <p>Winner: {match.winner}</p>
                        <p>Toss: {match.toss_won_by} won the toss and chose to {match.toss_decision}</p>
                    </div>
                ))
            ) : (
                <p>No matches available.</p>
            )}
        </div>
    );
};

export default Home;
