import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import '../styles/pages/BettingPage.css';

const BettingPage = () => {
  const { matchId } = useParams();

  const [totalRuns, setTotalRuns] = useState('');
  const [wickets, setWickets] = useState('');
  const [predictedWinner, setPredictedWinner] = useState('');
  const [odds, setOdds] = useState({ teamA: 1.80, teamB: 2.05 }); // Static for now

  const handleConfirmBet = async () => {
    if (!predictedWinner) {
      alert('Please select a winner!');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/bet/place`,
        {
          matchId,
          totalRuns: Number(totalRuns),
          wickets: Number(wickets),
          predictedWinner,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      alert(response.data.message);
    } catch (error) {
      console.error('Failed to place bet:', error);
      alert('Failed to place bet. Please try again.');
    }
  };

  const handleCancel = () => {
    setTotalRuns('');
    setWickets('');
    setPredictedWinner('');
  };

  return (
    <div className="betting-page">
      {/* 🔥 Match Details */}
      <h2 className="match-details">🔥 Match: Team A vs Team B (Live)</h2>

      {/* 📈 Current Odds */}
      <div className="odds-container">
        <p>📈 Current Odds:</p>
        <span>Team A: {odds.teamA}</span>
        <span>Team B: {odds.teamB}</span>
      </div>

      {/* 💰 Place Your Bet */}
      <div className="bet-form">
        {/* Predict Total Runs */}
        <div className="input-group">
          <label>🔹 Predict Total Runs:</label>
          <input
            type="number"
            value={totalRuns}
            onChange={(e) => setTotalRuns(e.target.value)}
            placeholder="Enter runs"
          />
        </div>

        {/* Predict Wickets */}
        <div className="input-group">
          <label>🔹 Predict Wickets:</label>
          <input
            type="number"
            value={wickets}
            onChange={(e) => setWickets(e.target.value)}
            placeholder="Enter wickets"
          />
        </div>

        {/* Predict Winner */}
        <div className="winner-buttons">
          <label>🔹 Predict Winner:</label>
          <button
            className={predictedWinner === 'Team A' ? 'active' : ''}
            onClick={() => setPredictedWinner('Team A')}
          >
            Team A
          </button>
          <button
            className={predictedWinner === 'Team B' ? 'active' : ''}
            onClick={() => setPredictedWinner('Team B')}
          >
            Team B
          </button>
        </div>
      </div>

      {/* ✅ Confirm and ❌ Cancel */}
      <div className="action-buttons">
        <button className="confirm-btn" onClick={handleConfirmBet}>
          ✅ Confirm Bet
        </button>
        <button className="cancel-btn" onClick={handleCancel}>
          ❌ Cancel
        </button>
      </div>
    </div>
  );
};

export default BettingPage;
