import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Home = () => {
  const [matches, setMatches] = useState([]);
  const [activeTab, setActiveTab] = useState("current");

  useEffect(() => {
    fetchMatches(new Date().toISOString().split("T")[0]); // Fetch today's matches by default
  }, []);

  const fetchMatches = (date) => {
    axios
      .get(`http://localhost:5001/api/matches/${date}`)
      .then((res) => setMatches(res.data))
      .catch((err) => console.error("Error fetching matches:", err));
  };

  return (
    <div>
      <h1>Live Matches</h1>
      <div>
        <button onClick={() => fetchMatches(new Date().toISOString().split("T")[0])}>
          Current
        </button>
        <button onClick={() => fetchMatches(new Date(new Date().setDate(new Date().getDate() - 1)).toISOString().split("T")[0])}>
          Recent
        </button>
        <button onClick={() => fetchMatches(new Date(new Date().setDate(new Date().getDate() + 1)).toISOString().split("T")[0])}>
          Upcoming
        </button>
      </div>
      {matches.length > 0 ? (
        matches.map((match) => (
          <div key={match.id} className="match-container">
            <Link className="match-link" to={`/match/${match.sport_event.id}`}>
              {match.sport_event.season.name}
            </Link>
            <p>Status: {match.sport_event_status.status}</p>
          </div>
        ))
      ) : (
        <p>No matches available.</p>
      )}
    </div>
  );
};

export default Home;
