import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import '../styles/pages/BettingPage.css';

const BettingPage = () => {
  const { matchId } = useParams();

  const [predictedWinner, setPredictedWinner] = useState('');
  const [odds, setOdds] = useState({ teamA: 1.80, teamB: 2.05 }); // Static for now

  const handleConfirmBet = async () => {
    if (!predictedWinner) {
      alert('Please select a winner!');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/bet/place`,
        {
          matchId,
          team: predictedWinner,
          amount: 100, // Example amount
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      alert(response.data.message);
    } catch (error) {
      console.error('Failed to place bet:', error);
      alert('Failed to place bet. Please try again.');
    }
  };

  const handleCancel = () => {
    setPredictedWinner('');
  };

  return (
    <div className="betting-page">
      {/* üî• Match Details */}
      <h2 className="match-details">üî• Match: Team A vs Team B (Live)</h2>

      {/* üìà Current Odds */}
      <div className="odds-container">
        <p>üìà Current Odds:</p>
        <span className="team-odds">Team A: {odds.teamA}</span>
        <span className="team-odds">Team B: {odds.teamB}</span>
      </div>

      {/* üîπ Predict Winner */}
      <div className="winner-buttons">
        <label>üîπ Predict Winner:</label>
        <button
          className={predictedWinner === 'Team A' ? 'active' : ''}
          onClick={() => setPredictedWinner('Team A')}
        >
          Team A
        </button>
        <button
          className={predictedWinner === 'Team B' ? 'active' : ''}
          onClick={() => setPredictedWinner('Team B')}
        >
          Team B
        </button>
      </div>

      {/* ‚úÖ Confirm and ‚ùå Cancel */}
      <div className="action-buttons">
        <button className="confirm-btn" onClick={handleConfirmBet}>
          ‚úÖ Confirm Bet
        </button>
        <button className="cancel-btn" onClick={handleCancel}>
          ‚ùå Cancel
        </button>
      </div>
    </div>
  );
};

export default BettingPage;
