import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getMatchDetails } from '../api/apiService';
import Scorecard from '../components/Scorecard';
import Commentary from '../components/Commentary';

const MatchDetailsPage = () => {
  const { matchId } = useParams();
  const [match, setMatch] = useState(null);

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const data = await getMatchDetails(matchId);
        setMatch(data);
      } catch (error) {
        console.error('Failed to load match details:', error);
      }
    };

    fetchMatchDetails();
  }, [matchId]);

  if (!match) return <p>Loading match details...</p>;

  return (
    <div className="match-details-container">
      <h2>{match.league} - {match.home_team} vs {match.away_team}</h2>
      <p>üìÖ Date: {new Date(match.scheduled).toLocaleString()}</p>
      <p>üìç Venue: {match.venue || 'Unknown Venue'}</p>
      <p>üî• Status: {match.status}</p>

      {/* ‚úÖ Home and Away Team Batting */}
      <Scorecard title={`${match.home_team} Batting`} data={match.batting_scorecard?.home_team || []} />
      <Scorecard title={`${match.away_team} Batting`} data={match.batting_scorecard?.away_team || []} />

      {/* ‚úÖ Home and Away Team Bowling */}
      <Scorecard title={`${match.home_team} Bowling`} data={match.bowling_scorecard?.home_team || []} isBowling />
      <Scorecard title={`${match.away_team} Bowling`} data={match.bowling_scorecard?.away_team || []} isBowling />

      {/* ‚úÖ Commentary */}
      <Commentary commentary={match.commentary || []} />
    </div>
  );
};

export default MatchDetailsPage;
