import React, { useState } from 'react';

const BettingPage = ({ matchId }) => {
  const [amount, setAmount] = useState('');

  const handlePlaceBet = async () => {
    try {
      const response = await fetch(`http://localhost:5001/api/user/bet`, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ matchId, amount }),
      });

      if (response.ok) {
        alert('Bet placed successfully!');
      } else {
        console.error('Failed to place bet');
      }
    } catch (error) {
      console.error('Error placing bet:', error);
    }
  };

  return (
    <div className="betting-page">
      <h2>Place Your Bet</h2>
      <input
        type="number"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
        placeholder="Enter Amount"
      />
      <button onClick={handlePlaceBet} className="bet-btn">ðŸ’° Bet Now</button>
    </div>
  );
};

export default BettingPage;
