import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/pages/MatchDetailsPage.css';
import Scorecard from '../components/Scorecard';
import Commentary from '../components/Commentary';

const MatchDetailsPage = () => {
  const { matchId } = useParams();
  const [matchDetails, setMatchDetails] = useState(null);
  const [activeTab, setActiveTab] = useState('scorecard');

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/match/${matchId}`);
        const data = await response.json();
        setMatchDetails(data);
      } catch (error) {
        console.error('Failed to load match details:', error);
      }
    };

    fetchMatchDetails();
  }, [matchId]);

  if (!matchDetails) {
    return <p>Loading match details...</p>;
  }

  const {
    league,
    home_team,
    away_team,
    scheduled,
    venue,
    status,
    toss,
    match_winner,
    home_score,
    away_score
  } = matchDetails;

  return (
    <div className="match-details-container">
      <h1>{home_team} vs {away_team}</h1>
      <p>üìÖ <strong>Date:</strong> {new Date(scheduled).toLocaleString()}</p>
      <p>üìç <strong>Venue:</strong> {venue || 'Unknown Venue'}</p>
      <p>üî• <strong>Status:</strong> {status}</p>
      {toss && <p>üèÜ <strong>Toss:</strong> {toss}</p>}
      {match_winner && <p>ü•á <strong>Winner:</strong> {match_winner}</p>}
      {home_score && away_score && (
        <p>üèè <strong>Score:</strong> {home_score} - {away_score}</p>
      )}

      {/* ‚úÖ Tab Switcher */}
      <div className="tab-container">
        <button
          className={`tab ${activeTab === 'scorecard' ? 'active' : ''}`}
          onClick={() => setActiveTab('scorecard')}
        >
          Scorecard
        </button>
        <button
          className={`tab ${activeTab === 'commentary' ? 'active' : ''}`}
          onClick={() => setActiveTab('commentary')}
        >
          Commentary
        </button>
      </div>

      {activeTab === 'scorecard' ? (
        <Scorecard data={matchDetails.scorecard} />
      ) : (
        <Commentary commentary={matchDetails.commentary} />
      )}
    </div>
  );
};

export default MatchDetailsPage;
