import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../components/Home.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            let date;
            if (activeTab === "current") date = "2025-01-31";
            else if (activeTab === "recent") date = "2025-01-29";
            else if (activeTab === "upcoming") date = "2025-02-02";

            try {
                console.log(`ðŸŸ¡ Fetching matches for ${activeTab}: ${date}`);
                const response = await axios.get(`http://localhost:5001/api/matches/${date}`);
                setMatches(response.data);
            } catch (err) {
                setError("Failed to load match data.");
            }
        };
        fetchMatches();
    }, [activeTab]);

    return (
        <div>
            <h1>Live Matches</h1>
            <div className="tabs">
                <button onClick={() => setActiveTab("current")} className={activeTab === "current" ? "active" : ""}>
                    Current
                </button>
                <button onClick={() => setActiveTab("recent")} className={activeTab === "recent" ? "active" : ""}>
                    Recent
                </button>
                <button onClick={() => setActiveTab("upcoming")} className={activeTab === "upcoming" ? "active" : ""}>
                    Upcoming
                </button>
            </div>

            {error ? <h3>{error}</h3> : (
                matches.length > 0 ? (
                    matches
                        .filter(match => match !== null)  // **ðŸ”¥ Remove null matches**
                        .map((match, index) => (
                            <div key={match.id || index} className="match-card">
                                <Link to={`/match/${match.id}`} className="match-link">
                                    {match.teamA} vs {match.teamB}
                                </Link>
                                <p>League: {match.league}</p>
                                <p>Venue: {match.venue}</p>
                                <p>Scheduled: {match.scheduled}</p>
                                <p>Status: {match.status}</p>
                                <p>Toss Winner: {match.toss_winner} ({match.toss_decision})</p>
                                <p>Match Winner: {match.winner}</p>
                            </div>
                        ))
                ) : (
                    <h3>No matches available.</h3>
                )
            )}
        </div>
    );
};

export default Home;
