import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL + "/user";

// ✅ Register User
export const registerUser = async (userData) => {
  console.log("Sending registration payload:", userData); // ✅ Debugging

  try {
    const { data } = await axios.post(`${API_URL}/register`, userData);
    console.log("Registration success:", data); // ✅ Debugging
    return data;
  } catch (error) {
    console.error("Registration failed:", error.response?.data || error.message);
    throw new Error("Registration failed");
  }
};

// ✅ Login User
export const loginUser = async (userData) => {
  console.log("Sending login payload:", userData); // ✅ Debugging

  try {
    const { data } = await axios.post(`${API_URL}/login`, userData);

    if (data.token) {
      localStorage.setItem("token", data.token);
    }

    console.log("Login success:", data); // ✅ Debugging
    return data;
  } catch (error) {
    console.error("Login failed:", error.response?.data || error.message);
    throw new Error("Login failed");
  }
};

// ✅ Logout User
export const logoutUser = async () => {
  console.log("Sending logout request"); // ✅ Debugging

  try {
    await axios.post(`${API_URL}/logout`);
    localStorage.removeItem("token");
  } catch (error) {
    console.error("Logout failed:", error.response?.data || error.message);
    throw new Error("Logout failed");
  }
};

// ✅ Get User Profile
export const getProfile = async (token) => {
  console.log("Fetching user profile"); // ✅ Debugging

  try {
    const { data } = await axios.get(`${API_URL}/profile`, {
      headers: { Authorization: `Bearer ${token}` },
    });

    console.log("Profile loaded:", data);
    return data;
  } catch (error) {
    console.error("Failed to load user profile:", error.response?.data || error.message);
    throw new Error("Failed to load user profile");
  }
};

// ✅ Request Credits
export const requestCredits = async (amount) => {
  try {
    const token = localStorage.getItem("token");
    const { data } = await axios.post(
      `${API_URL}/request-credits`,
      { amount },
      { headers: { Authorization: `Bearer ${token}` } }
    );
    return data;
  } catch (error) {
    throw error.response?.data?.error || "Failed to request credits";
  }
};
