import React from "react";
import { Link } from "react-router-dom";

function MatchCard({ match }) {
    const isLive = match.status === "live";
    const isEnded = match.status === "ended";

    return (
        <div className="col-md-4">
            <div className={`card ${isLive ? "border-success" : isEnded ? "border-danger" : "border-primary"}`}>
                <div className="card-body">
                    <h5 className="card-title">{match.name}</h5>
                    <p className="card-text">
                        Status: <strong className={isLive ? "text-success" : isEnded ? "text-danger" : "text-primary"}>{match.status}</strong>
                    </p>
                    <p>Score: {match.display_score || "N/A"}</p>
                    <Link to={`/match/${match.id}`} className="btn btn-primary">View Details</Link>
                </div>
            </div>
        </div>
    );
}

export default MatchCard;
