import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "../components/Home.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            try {
                const response = await axios.get(`http://localhost:5001/api/matches/${activeTab}`);
                setMatches(Array.isArray(response.data) ? response.data : []);
            } catch (err) {
                console.error("Failed to fetch matches:", err);
                setError("Failed to load matches.");
            }
        };
        fetchMatches();
    }, [activeTab]);

    return (
        <div className="home">
            <h1>Live Matches</h1>
            <div className="tabs">
                {["current", "recent", "upcoming"].map((tab) => (
                    <button key={tab} className={activeTab === tab ? "active" : ""} onClick={() => setActiveTab(tab)}>
                        {tab.charAt(0).toUpperCase() + tab.slice(1)}
                    </button>
                ))}
            </div>
            {error ? <p className="error">{error}</p> : matches.length > 0 ? (
                matches.map((match) => (
                    <div key={match.sport_event.id} className="match-card">
                        <Link to={`/match/${match.sport_event.id}`}>
                            <h2>{match.sport_event.name || "Match"}</h2>
                        </Link>
                        <p>League: {match.sport_event.season?.name || "N/A"}</p>
                        <p>Scheduled: {match.sport_event.scheduled}</p>
                        <p>Status: {match.sport_event_status?.status || "N/A"}</p>
                        <p>Winner: {match.sport_event_status?.winner_id || "TBD"}</p>
                        <p>Toss: {match.sport_event_status?.toss_won_by || "TBD"} chose to {match.sport_event_status?.toss_decision || "TBD"}</p>
                    </div>
                ))
            ) : (
                <p>No matches available.</p>
            )}
        </div>
    );
};

export default Home;
