import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../components/Home.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchMatches = async () => {
            try {
                const date = "2025-01-30";
                const response = await axios.get(`http://localhost:5001/api/matches/${date}`);
                console.log("ðŸŸ¢ [FRONTEND] Matches received:", response.data);
                setMatches(response.data);
            } catch (err) {
                console.error("ðŸ”´ [ERROR] Could not fetch matches:", err);
                setError("Failed to load matches.");
            }
        };
        fetchMatches();
    }, []);

    return (
        <div>
            <h1>Live Matches</h1>
            {error && <p>{error}</p>}
            {matches.length > 0 ? (
                matches.map((match) => (
                    <div key={match.sport_event.id} className="match-card">
                        <Link to={`/match/${match.sport_event.id}`}>
                            <h2>{match.sport_event.teamA} vs {match.sport_event.teamB}</h2>
                        </Link>
                        <p>League: {match.sport_event.season.name}</p>
                        <p>Scheduled: {new Date(match.sport_event.scheduled).toLocaleString()}</p>
                        <p>Status: {match.sport_event.status}</p>
                        <p>Winner: {match.sport_event_status.winner_id || "TBD"}</p>
                        <p>Toss: {match.sport_event_status.toss_won_by || "TBD"} chose to {match.sport_event_status.toss_decision}</p>
                    </div>
                ))
            ) : (
                <p>No matches available.</p>
            )}
        </div>
    );
};

export default Home;
