import React from 'react';
import '../styles/components/Scorecard.css';

const Scorecard = ({ title, data }) => {
  if (!data || data.length === 0) {
    return <p>No scorecard data available.</p>;
  }

  return (
    <div className="scorecard">
      <h2>{title}</h2>
      <table>
        <thead>
          <tr>
            {Object.keys(data[0]).map((key) => (
              <th key={key}>{key.replace('_', ' ').toUpperCase()}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((player, index) => (
            <tr key={index}>
              {Object.values(player).map((value, idx) => (
                <td key={idx}>{value}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Scorecard;
