import { createContext, useContext, useState, useEffect } from 'react';
import { loginUser, logoutUser, getProfile } from '../api/authService';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    const loadUser = async () => {
      try {
        console.log('Fetching user profile'); // ✅ Debugging
        const profile = await getProfile();
        setUser(profile);
        setIsAuthenticated(true);
        setIsAdmin(profile.role === 'admin');
      } catch (error) {
        console.error('Failed to load user profile:', error);
        setIsAuthenticated(false);
      }
    };

    loadUser();
  }, []);


const login = async (credentials) => {
  const data = await loginUser(credentials);
  localStorage.setItem('token', data.token);
  setUser(data.user);
  setIsAdmin(data.user.role==="admin")
  const profile = await getProfile(); // ✅ Fix sync state after login
  setUser(profile);
  setIsAuthenticated(true);
  setIsAdmin(profile.role === 'admin');
};

  const logout = async () => {
    try {
      console.log('Sending logout request'); // ✅ Debugging
      await logoutUser();
      localStorage.removeItem('token');
      setUser(null);
      setIsAuthenticated(false);
      setIsAdmin(false);
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  return (
    <AuthContext.Provider value={{ user, isAuthenticated, isAdmin, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);

export default AuthContext;
