import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../components/Home.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            let date = new Date().toISOString().split("T")[0];
            if (activeTab === "recent") date = "2025-01-30"; // Example for testing
            if (activeTab === "upcoming") date = "2025-02-01";

            try {
                console.log(`ðŸŸ¡ Fetching matches for ${activeTab}: ${date}`);
                const response = await axios.get(`http://localhost:5001/api/matches/${date}`);
                setMatches(response.data);
            } catch (err) {
                setError("Failed to fetch match data.");
            }
        };
        fetchMatches();
    }, [activeTab]);

    return (
        <div className="home">
            <div className="tabs">
                <button onClick={() => setActiveTab("current")}>Current</button>
                <button onClick={() => setActiveTab("recent")}>Recent</button>
                <button onClick={() => setActiveTab("upcoming")}>Upcoming</button>
            </div>

            {error && <h2>{error}</h2>}

            {matches.length > 0 ? (
                matches.map((match, index) => (
                    <div key={match.id || index} className="match-card">
                        <Link to={`/match/${match.id}`}>
                            {match.teams}
                        </Link>
                        <p>League: {match.league}</p>
                        <p>Scheduled: {match.scheduled}</p>
                        <p>Status: {match.status}</p>
                    </div>
                ))
            ) : (
                <h3>No matches available.</h3>
            )}
        </div>
    );
};

export default Home;
