import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMatchDetails = async () => {
            try {
                console.log(`üü° Fetching match details for: ${matchId}`);
                const response = await axios.get(`http://localhost:5001/api/match/${matchId}`);
                setMatchDetails(response.data);
            } catch (err) {
                setError("Failed to load match details");
                console.error("‚ùå Error fetching match details:", err);
            }
        };

        fetchMatchDetails();
    }, [matchId]);

    if (error) return <h2>{error}</h2>;
    if (!matchDetails) return <h2>Loading...</h2>;

    const { teams, league, venue, scheduled, status, toss_winner, toss_decision, winner, scores } = matchDetails;

    // Mapping team names correctly
    const teamA = teams ? teams[0] : "Unknown Team A";
    const teamB = teams ? teams[1] : "Unknown Team B";

    // Fetching scores based on period_scores
    let teamA_Score = "N/A", teamB_Score = "N/A";
    if (scores && scores.length > 0) {
        if (scores.length === 2) {
            // Assuming first inning belongs to away team and second inning to home team
            teamB_Score = scores[0].display_score; // Away Team (First Inning)
            teamA_Score = scores[1].display_score; // Home Team (Second Inning)
        }
    }

    return (
        <div className="match-details">
            <h1>Match Details</h1>
            <h2>{teamA} vs {teamB}</h2>
            <p><strong>League:</strong> {league}</p>
            <p><strong>Venue:</strong> {venue || "Unknown"}</p>
            <p><strong>Scheduled:</strong> {scheduled}</p>
            <p><strong>Status:</strong> {status}</p>
            <p><strong>Toss Winner:</strong> {toss_winner} ({toss_decision})</p>
            <p><strong>Match Winner:</strong> {winner || "N/A"}</p>

            <h3>Scores:</h3>
            <p><strong>{teamA}:</strong> {teamA_Score}</p>
            <p><strong>{teamB}:</strong> {teamB_Score}</p>
        </div>
    );
};

export default MatchPage;
