import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../components/Home.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            const date = activeTab === "current" ? "2025-01-31" : "2025-01-30";
            try {
                console.log(`ðŸŸ¡ Fetching matches for ${activeTab}: ${date}`);
                const response = await axios.get(`http://localhost:5001/api/matches/${date}`);
                setMatches(response.data);
            } catch (err) {
                setError("Failed to load matches.");
            }
        };
        fetchMatches();
    }, [activeTab]);

    return (
        <div>
            <h1>Live Matches</h1>
            <div>
                <button onClick={() => setActiveTab("current")}>Current</button>
                <button onClick={() => setActiveTab("recent")}>Recent</button>
                <button onClick={() => setActiveTab("upcoming")}>Upcoming</button>
            </div>
            {matches.map((match, index) => (
                <div key={index}>
                    <Link to={`/match/${match.id}`}>
                        {match.teamA} vs {match.teamB}
                    </Link>
                    <p>League: {match.league}</p>
                    <p>Scheduled: {match.scheduled}</p>
                    <p>Status: {match.status}</p>
                </div>
            ))}
        </div>
    );
};

export default Home;
