import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../components/Home.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            try {
                const today = new Date().toISOString().split("T")[0];
                let endpoint = "";

                if (activeTab === "current") {
                    endpoint = today;
                } else if (activeTab === "recent") {
                    const yesterday = new Date();
                    yesterday.setDate(yesterday.getDate() - 1);
                    endpoint = yesterday.toISOString().split("T")[0];
                } else {
                    const tomorrow = new Date();
                    tomorrow.setDate(tomorrow.getDate() + 1);
                    endpoint = tomorrow.toISOString().split("T")[0];
                }

                const response = await axios.get(`http://localhost:5001/api/matches/${endpoint}`);
                console.log("ðŸŸ¢ [FRONTEND] Matches received:", response.data);
                setMatches(response.data);
            } catch (err) {
                console.error("ðŸ”´ [ERROR] Could not fetch matches:", err);
                setError("Failed to load matches.");
            }
        };

        fetchMatches();
    }, [activeTab]);

    return (
        <div className="home">
            <h1>Live Matches</h1>
            <div className="tabs">
                <button className={activeTab === "current" ? "active" : ""} onClick={() => setActiveTab("current")}>Current</button>
                <button className={activeTab === "recent" ? "active" : ""} onClick={() => setActiveTab("recent")}>Recent</button>
                <button className={activeTab === "upcoming" ? "active" : ""} onClick={() => setActiveTab("upcoming")}>Upcoming</button>
            </div>

            {error && <h2>{error}</h2>}

            <div className="match-list">
                {matches.length > 0 ? matches.map(match => (
                    <div className="match-card" key={match.sport_event.id}>
                        <Link to={`/match/${match.sport_event.id}`}>
                            <h2>{match.sport_event.competitors[0].name} vs {match.sport_event.competitors[1].name}</h2>
                        </Link>
                        <p>League: {match.sport_event.tournament?.name || "N/A"}</p>
                        <p>Scheduled: {new Date(match.sport_event.scheduled).toLocaleString()}</p>
                        <p>Status: {match.sport_event_status.status}</p>
                        <p>Winner: {match.sport_event_status.winner || "TBD"}</p>
                        <p>Toss: {match.sport_event_status.toss_winner || "TBD"} chose to {match.sport_event_status.toss_decision || "TBD"}</p>
                    </div>
                )) : <p>No matches available.</p>}
            </div>
        </div>
    );
};

export default Home;
