import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./MatchPage.css";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMatchDetails = async () => {
            setLoading(true);
            try {
                const response = await axios.get(`http://localhost:5001/api/match/${matchId}`);
                setMatchDetails(response.data);
                setError(null);
            } catch (err) {
                console.error("❌ Error fetching match details:", err);
                setError("Failed to load match details.");
            } finally {
                setLoading(false);
            }
        };

        fetchMatchDetails();
    }, [matchId]);

    if (loading) return <h2>Loading match details...</h2>;
    if (error) return <h2>{error}</h2>;

    return (
        <div className="match-detail">
            <h1>Match Details</h1>
            <h2>{matchDetails.sport_event.competitors[0].name} vs {matchDetails.sport_event.competitors[1].name}</h2>
            <p>League: {matchDetails.sport_event.sport_event_context.competition.name}</p>
            <p>Scheduled: {new Date(matchDetails.sport_event.scheduled).toLocaleString()}</p>
            <p>Status: {matchDetails.sport_event_status.match_status}</p>
            <p>Winner: {matchDetails.sport_event_status.winner_id || "TBD"}</p>
            <p>Toss: {matchDetails.sport_event_status.toss_winner_id || "TBD"} chose to {matchDetails.sport_event_status.toss_decision || "TBD"}</p>
        </div>
    );
};

export default MatchPage;
