import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import BettingSection from "../components/BettingSection";

function MatchPage() {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:5001/api/match/${matchId}`)
            .then(response => setMatchDetails(response.data))
            .catch(error => console.error("Error fetching match details:", error));
    }, [matchId]);

    if (!matchDetails) return <p>Loading match details...</p>;

    return (
        <div className="container mt-4">
            <h2>{matchDetails.name}</h2>
            <p><strong>Status:</strong> {matchDetails.status}</p>
            <p><strong>Score:</strong> {matchDetails.display_score || "N/A"}</p>

            {matchDetails.status === "live" && <BettingSection matchId={matchId} />}
        </div>
    );
}

export default MatchPage;
