import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/pages/MatchDetailsPage.css';
import Commentary from '../components/Commentary';
import Scorecard from '../components/Scorecard';
import { getMatchDetails } from '../api/apiService';

const MatchDetailsPage = () => {
  const { matchId } = useParams();
  const [matchDetails, setMatchDetails] = useState(null);
  const [activeTab, setActiveTab] = useState('scorecard');

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const data = await getMatchDetails(matchId);
        setMatchDetails(data);
      } catch (error) {
        console.error('Failed to fetch match details:', error);
      }
    };

    fetchMatchDetails();
  }, [matchId]);

  if (!matchDetails) return <div className="loading">Loading match details...</div>;

  const {
    scheduled, venue, status, home_team, away_team,
    toss_winner, toss_decision, match_winner, home_score, away_score
  } = matchDetails;

  return (
    <div className="match-details-container">
      <h1>{`${home_team} vs ${away_team}`}</h1>
      <div className="match-info">
        <p>üìÖ Date: {new Date(scheduled).toLocaleString()}</p>
        <p>üìç Venue: {venue || 'Unknown Venue'}</p>
        <p>üî• Status: {status}</p>
        {toss_winner && (
          <p>üèÜ Toss: {`${toss_winner} chose to ${toss_decision}`}</p>
        )}
        {match_winner && (
          <p>üèÖ Winner: {match_winner}</p>
        )}
        {home_score && away_score && (
          <p>üèè Score: {home_score} - {away_score}</p>
        )}
      </div>

      <div className="tab-container">
        <button
          className={activeTab === 'scorecard' ? 'active' : ''}
          onClick={() => setActiveTab('scorecard')}
        >
          Scorecard
        </button>
        <button
          className={activeTab === 'commentary' ? 'active' : ''}
          onClick={() => setActiveTab('commentary')}
        >
          Commentary
        </button>
      </div>

      {activeTab === 'scorecard' && <Scorecard data={matchDetails} />}
      {activeTab === 'commentary' && <Commentary data={matchDetails?.commentary} />}
    </div>
  );
};

export default MatchDetailsPage;
