import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            let date;
            if (activeTab === "current") date = "2025-01-30";
            else if (activeTab === "recent") date = "2025-01-29";
            else if (activeTab === "upcoming") date = "2025-01-31";

            console.log(`üü° Fetching matches for ${activeTab}: ${date}`);

            try {
                const response = await axios.get(`http://localhost:5001/api/matches/${date}`);
                console.log("‚úÖ API Response:", response.data);

                if (!Array.isArray(response.data)) {
                    console.error("‚ùå Invalid API response:", response.data);
                    setError("Invalid data received.");
                    return;
                }

                setMatches(response.data);
            } catch (err) {
                console.error("‚ùå Fetch error:", err.message);
                setError("Failed to fetch matches.");
            }
        };

        fetchMatches();
    }, [activeTab]);

    return (
        <div>
            <h1>Live Matches</h1>
            <button onClick={() => setActiveTab("current")} style={{ backgroundColor: activeTab === "current" ? "blue" : "gray" }}>Current</button>
            <button onClick={() => setActiveTab("recent")} style={{ backgroundColor: activeTab === "recent" ? "blue" : "gray" }}>Recent</button>
            <button onClick={() => setActiveTab("upcoming")} style={{ backgroundColor: activeTab === "upcoming" ? "blue" : "gray" }}>Upcoming</button>

            {error && <h2>{error}</h2>}

            {matches.length > 0 ? (
                matches.map((match, index) => {
                    const teamA = match.competitors?.[0]?.name || "Unknown Team A";
                    const teamB = match.competitors?.[1]?.name || "Unknown Team B";
                    return (
                        <div key={match.id || index}>
                            <Link to={`/match/${match.id}`}>
                                {teamA} vs {teamB}
                            </Link>
                            <p>League: {match.season?.name || "N/A"}</p>
                            <p>Scheduled: {match.scheduled}</p>
                            <p>Status: {match.sport_event_status?.status || "Scheduled"}</p>
                        </div>
                    );
                })
            ) : (
                <h3>No matches available.</h3>
            )}
        </div>
    );
};

export default Home;
