import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMatchDetails = async () => {
            try {
                console.log(`ðŸ”— Fetching match details for: ${matchId}`);
                const response = await axios.get(`http://localhost:5001/matches/${matchId}`);
                setMatchDetails(response.data);
            } catch (err) {
                setError("Failed to load match details.");
            }
        };
        fetchMatchDetails();
    }, [matchId]);

    if (error) return <h2>{error}</h2>;
    if (!matchDetails) return <h2>Loading...</h2>;

    // Extract Competitor Names
    const competitors = matchDetails.teams || [];
    const homeTeam = competitors[0]?.name || "Unknown Home Team";
    const awayTeam = competitors[1]?.name || "Unknown Away Team";

    // Extract Toss Winner
    const tossWinner = competitors.find(team => team.id === matchDetails.toss_winner)?.name || "Unknown";

    // Extract Match Winner
    const matchWinner = competitors.find(team => team.id === matchDetails.winner)?.name || "N/A";

    // Extract Scores
    const scores = matchDetails.scores || [];
    const homeScore = scores[1]?.display_score || "N/A";
    const awayScore = scores[0]?.display_score || "N/A";

    return (
        <div className="match-details">
            <h1>Match Details</h1>
            <h2>{homeTeam} vs {awayTeam}</h2>
            <p><strong>League:</strong> {matchDetails.league}</p>
            <p><strong>Venue:</strong> {matchDetails.venue || "Unknown Venue"}</p>
            <p><strong>Scheduled:</strong> {matchDetails.scheduled}</p>
            <p><strong>Status:</strong> {matchDetails.status}</p>
            <p><strong>Toss Winner:</strong> {tossWinner} ({matchDetails.toss_decision})</p>
            <p><strong>Match Winner:</strong> {matchWinner}</p>

            <h3>Scores:</h3>
            <p><strong>{homeTeam}:</strong> {homeScore}</p>
            <p><strong>{awayTeam}:</strong> {awayScore}</p>
        </div>
    );
};

export default MatchPage;
