import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../components/Home.css"

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [error, setError] = useState(null);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        const fetchMatches = async () => {
            let date;
            if (activeTab === "current") date = "2025-01-30";
            else if (activeTab === "recent") date = "2025-01-29";
            else if (activeTab === "upcoming") date = "2025-01-31";

            try {
                console.log(`üü° Fetching matches for ${activeTab}: ${date}`);
                const response = await axios.get(`http://localhost:5001/api/matches/${date}`);

                if (!Array.isArray(response.data)) {
                    console.error("‚ùå Invalid API response:", response.data);
                    setError("Invalid data received.");
                    return;
                }

                setMatches(response.data);
            } catch (err) {
                console.error("‚ùå Error fetching matches:", err);
                setError("Failed to fetch matches.");
            }
        };

        fetchMatches();
    }, [activeTab]);

    return (
        <div>
            <h1>Live Matches</h1>
            <button onClick={() => setActiveTab("current")}>Current</button>
            <button onClick={() => setActiveTab("recent")}>Recent</button>
            <button onClick={() => setActiveTab("upcoming")}>Upcoming</button>

            {error && <h2>{error}</h2>}
            {matches.length === 0 ? <h2>No matches found.</h2> : (
                matches.map((match) => (
                    <div key={match.id}>
                        <Link to={`/match/${match.id}`}>{match.name}</Link>
                        <p>League: {match.league}</p>
                        <p>Scheduled: {match.scheduled}</p>
                        <p>Status: {match.status}</p>
                        <p>Winner: {match.winner}</p>
                        <p>Toss: {match.toss_winner}</p>
                    </div>
                ))
            )}
        </div>
    );
};

export default Home;
