const Commentary = ({ commentary }) => {
  if (!commentary) return <p>No commentary available</p>;

  return (
    <div className="commentary">
      <h3>üó£Ô∏è Commentary</h3>
      {commentary?.map((comment, index) => (
        <div key={index} className="comment">
          <p><strong>Over:</strong> {comment.over}, <strong>Ball:</strong> {comment.ball}</p>
          <p><strong>Batsman:</strong> {comment.batsman} | <strong>Bowler:</strong> {comment.bowler}</p>
          <p><strong>Runs:</strong> {comment.runs}</p>
          <p>{comment.description}</p>
        </div>
      ))}
    </div>
  );
};

export default Commentary;
