import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "../styles.css";

const Home = () => {
    const [matches, setMatches] = useState([]);
    const [activeTab, setActiveTab] = useState("current");

    useEffect(() => {
        fetchMatches("current");
    }, []);

    const fetchMatches = async (type) => {
        setActiveTab(type);
        let date = new Date();
        if (type === "recent") date.setDate(date.getDate() - 1);
        if (type === "upcoming") date.setDate(date.getDate() + 1);

        const formattedDate = date.toISOString().split("T")[0];

        try {
            const response = await axios.get(`http://localhost:5000/api/matches/${formattedDate}`);
            console.log("ðŸŸ¢ [FRONTEND] Matches received:", response.data);
            setMatches(response.data.results);
        } catch (error) {
            console.error("ðŸ”´ [ERROR] Could not fetch matches:", error);
        }
    };

    return (
        <div>
            <h1>Live Matches</h1>
            <div className="tabs">
                <button onClick={() => fetchMatches("current")} className={activeTab === "current" ? "active" : ""}>Current</button>
                <button onClick={() => fetchMatches("recent")} className={activeTab === "recent" ? "active" : ""}>Recent</button>
                <button onClick={() => fetchMatches("upcoming")} className={activeTab === "upcoming" ? "active" : ""}>Upcoming</button>
            </div>

            {matches.length > 0 ? matches.map(match => (
                <div key={match.sport_event.id} className="match-card">
                    <Link to={`/match/${match.sport_event.id}`}>
                        <h2>{match.sport_event.sport_event_context.competitors[0].name} vs {match.sport_event.sport_event_context.competitors[1].name}</h2>
                    </Link>
                    <p>League: {match.sport_event.season.name}</p>
                    <p>Scheduled: {new Date(match.sport_event.scheduled).toLocaleString()}</p>
                    <p>Status: {match.sport_event.status}</p>
                    <p>Winner: {match.sport_event_status.winner_id || "TBD"}</p>
                    <p>Toss: {match.sport_event_status.toss_won_by || "TBD"} won the toss</p>
                </div>
            )) : <p>No matches available.</p>}
        </div>
    );
};

export default Home;
