import { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import "../styles/pages/AdminDashboard.css";

const AdminDashboard = () => {
  const { user, isAuthenticated } = useAuth();
  const [users, setUsers] = useState([]);
  const [creditRequests, setCreditRequests] = useState([]);
  const [bets, setBets] = useState([]);

  useEffect(() => {
    if (isAuthenticated && user?.role === 'admin') {
      loadUsers();
      loadCreditRequests();
      loadBets();
    }
  }, [isAuthenticated, user]);

  const loadUsers = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/users`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setUsers(response.data);
    } catch (error) {
      console.error("Failed to load users:", error);
    }
  };

  const loadCreditRequests = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/credit-requests`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setCreditRequests(response.data);
    } catch (error) {
      console.error("Failed to load credit requests:", error);
    }
  };

  const loadBets = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/bets`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setBets(response.data);
    } catch (error) {
      console.error("Failed to load bets:", error);
    }
  };

  const handleApprove = async (id) => {
    try {
      await axios.put(`${process.env.REACT_APP_API_URL}/api/admin/approve-credit/${id}`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      loadCreditRequests(); // Reload after approving
    } catch (error) {
      console.error("Failed to approve credit request:", error);
    }
  };

  const handleReject = async (id, reason) => {
    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/api/admin/credit-requests/${id}`, {
        status: "rejected",
        reason,
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      loadCreditRequests(); // Reload after rejecting
    } catch (error) {
      console.error("Failed to reject credit request:", error);
    }
  };

  return (
    <div className="admin-dashboard">
      <h2>🏆 Admin Dashboard</h2>

      {/* Users Table */}
      <div className="admin-section">
        <h3>👥 Users</h3>
        <table>
          <thead>
            <tr>
              <th>Username</th>
              <th>Phone</th>
              <th>Credits</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user._id}>
                <td>{user.username}</td>
                <td>{user.phone}</td>
                <td>{user.credits}</td>
                <td>
                  <button onClick={() => handleRemoveUser(user._id)} className="remove-btn">
                    ❌ Remove
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Credit Requests Table */}
      <div className="admin-section">
        <h3>💰 Credit Requests</h3>
        <table>
          <thead>
            <tr>
              <th>Username</th>
              <th>Amount</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {creditRequests.map(request => (
              <tr key={request._id}>
                <td>{request.username}</td>
                <td>{request.amount}</td>
                <td>{request.status}</td>
                <td>
                  {request.status === 'pending' ? (
                    <>
                      <button onClick={() => handleApprove(request._id)} className="approve-btn">✅ Approve</button>
                      <button onClick={() => handleReject(request._id, "Invalid payment proof")} className="reject-btn">❌ Reject</button>
                    </>
                  ) : (
                    <span>{request.status}</span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Bets Table */}
      <div className="admin-section">
        <h3>📊 Bets</h3>
        <table>
          <thead>
            <tr>
              <th>Username</th>
              <th>Match</th>
              <th>Amount</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {bets.map(bet => (
              <tr key={bet._id}>
                <td>{bet.username}</td>
                <td>{bet.matchId}</td>
                <td>{bet.amount}</td>
                <td>{bet.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
