import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const MatchPage = () => {
    const { matchId } = useParams();
    const [matchDetails, setMatchDetails] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMatchDetails = async () => {
            try {
                console.log(`üü° Fetching match details for: ${matchId}`);
                const response = await axios.get(`http://localhost:5001/api/match/${matchId}`);

                if (!response.data.name) {
                    console.error("‚ùå Invalid match data:", response.data);
                    setError("Invalid match data received.");
                    return;
                }

                setMatchDetails(response.data);
            } catch (err) {
                console.error("‚ùå Error fetching match details:", err);
                setError("Failed to load match details.");
            }
        };

        fetchMatchDetails();
    }, [matchId]);

    if (error) return <h2>{error}</h2>;
    if (!matchDetails) return <h2>Loading match details...</h2>;

    return (
        <div>
            <h1>Match Details</h1>
            <h2>{matchDetails.name}</h2>
            <p>League: {matchDetails.league}</p>
            <p>Scheduled: {matchDetails.scheduled}</p>
            <p>Status: {matchDetails.status}</p>
            <p>Winner: {matchDetails.winner}</p>
            <p>Toss: {matchDetails.toss_winner}</p>
        </div>
    );
};

export default MatchPage;
