import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Scorecard from '../components/Scorecard';
import Commentary from '../components/Commentary';
import '../styles/pages/MatchDetailPage.css';

const MatchDetailPage = () => {
  const { matchId } = useParams();
  const [matchDetails, setMatchDetails] = useState({});
  const [activeTab, setActiveTab] = useState('scorecard');

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/match/${matchId}`);
        const data = await response.json();
        console.log('‚úÖ Match Details:', data); // Debugging purpose
        setMatchDetails(data || {});
      } catch (error) {
        console.error('Failed to load match details:', error);
      }
    };

    fetchMatchDetails();
  }, [matchId]);

  if (!matchDetails || Object.keys(matchDetails).length === 0) {
    return <p>Loading match details...</p>;
  }

  return (
    <div className="match-detail-container">
      <h2>{matchDetails.home_team_name} vs {matchDetails.away_team_name}</h2>
      <p>üìÖ <strong>Date:</strong> {new Date(matchDetails.date).toLocaleString()}</p>
      <p>üìç <strong>Venue:</strong> {matchDetails.venue || 'Unknown Venue'}</p>
      <p>üî• <strong>Status:</strong> {matchDetails.status}</p>
      {matchDetails.toss && <p>üèÜ <strong>Toss:</strong> {matchDetails.toss}</p>}
      {matchDetails.winner && <p>ü•á <strong>Winner:</strong> {matchDetails.winner}</p>}
      {matchDetails.home_score && matchDetails.away_score && (
        <p>üèè <strong>Score:</strong> {matchDetails.home_score} - {matchDetails.away_score}</p>
      )}

      {/* Tabs */}
      <div className="tab-container">
        <button
          className={`tab ${activeTab === 'scorecard' ? 'active' : ''}`}
          onClick={() => setActiveTab('scorecard')}
        >
          Scorecard
        </button>
        <button
          className={`tab ${activeTab === 'commentary' ? 'active' : ''}`}
          onClick={() => setActiveTab('commentary')}
        >
          Commentary
        </button>
      </div>

      {/* Conditional Rendering */}
      {activeTab === 'scorecard' ? (
        matchDetails.scorecard ? (
          <Scorecard data={matchDetails.scorecard} />
        ) : (
          <p>No scorecard data available.</p>
        )
      ) : (
        matchDetails.commentary ? (
          <Commentary commentary={matchDetails.commentary} />
        ) : (
          <p>No commentary available.</p>
        )
      )}
    </div>
  );
};

export default MatchDetailPage;
